import "./App.css";
import Input from "./components/input";
import Button from "./components/button";
import "./main-verificar.js";
import Verificar from "./main-verificar";
import React, { useState } from "react";

function App() {
  const [produto, setProduto] = useState({ nome: "", quantidade: 0, preco: 0 });
  return (
    <>
      <div className="App">
        <div>
          <h1 className="Text">Controle de Estoque</h1>
          <Input placeholder={"Nome do produto"} id={"produto"} />
          <Input placeholder={"Quantidade do produto"} id={"quantidade"} />
          <Input placeholder={"Preço do produto"} id={"preço"} />
        </div>
        <div className="Click">
          <Button
            name={"adicionar"}
            onClick={() => Verificar()}
            id={"adicionar"}
          >
            Adicionar
          </Button>
        </div>
        <div className="Click">
          <Button>Remover</Button>
        </div>
      </div>
      <div className="Itens">
        <span className="Span"> Intem</span>
        <span className="Span"> Quantidade</span>
        <span className="Span"> Preço</span>
        <span className="Span"> Remover</span>
        <div className="Divider" />
      </div>
    </>
  );
}

export default App;
