import "./App.css";
import Input from "./components/input";
import Button from "./components/button";
import Verificar from "./main-verificar";
import React, { useState } from "react";

function Home() {
  const [estoque, setEstoque] = useState([]);

  const [nome, setNome] = useState("");
  const [quantidade, setQuantidade] = useState(0);
  const [preco, setPreco] = useState(0);

  const adicionarProduto = (nome, quantidade, preco) => {
    const validar = Verificar(nome, quantidade, preco);

    if (validar) {
      const produto = { nome: nome, quantidade: quantidade, preco: preco };
      // altera o valor do estoque com o estoque antigo + o produto adicionado
      setEstoque([...estoque, produto]);
    }
  };
  return (
    <>
      <div className="App">
        <div>
          <h1 className="Text">Controle de Estoque</h1>
          <Input
            onChange={(value) => setNome(value)}
            placeholder={"Nome do produto"}
          />
          <Input
            onChange={(value) => setQuantidade(value)}
            placeholder={"Quantidade do produto"}
          />
          <Input
            onChange={(value) => setPreco(value)}
            placeholder={"Preço do produto"}
          />
        </div>
        <div className="Click">
          <Button
            name={"adicionar"}
            onClick={() => adicionarProduto(nome, quantidade, preco)}
          >
            Adicionar
          </Button>
        </div>
        <div className="Click">
          <Button>Remover</Button>
        </div>
      </div>
      <div className="Itens">
        <span className="Span"> Intem</span>
        <span className="Span"> Quantidade</span>
        <span className="Span"> Preço</span>
        <span className="Span"> Remover</span>
        <div className="Divider" />
        {estoque?.map((produto) => (
          <div>
            <table>
              <tr>
                <td>{produto.nome}</td>
                <td>{produto.quantidade} </td>
                <td>{produto.preco}</td>
              </tr>
            </table>
          </div>
        ))}
      </div>
    </>
  );
}

export default Home;
