var produto = document.getElementById("produto"); // variavel que recebe o elemento html 'input' com id #produto
var quantidade = document.getElementById("quantidade"); // variavel que recebe o elemento html 'input' com id #quantidade
var preço = document.getElementById("preço"); // variavel que recebe o elemento html 'input' com id #preço

function Verificar() {
  // funçao para verificar se todos os items estao preenchidos antes de adicionar
  console.log("Chamou");
  if (produto.value === "" && quantidade.value === 0 && preço.value <= 0) {
    window.alert(
      "Preencha todos os campos para adicionar um produto/item no estoque!"
    ); // exibe alertas de erro
  } else if (produto.value === "") {
    window.alert(
      'Preencha o "Nome do Item" para adicionar um produto/item no estoque!'
    );
  } else if (quantidade.value === 0) {
    window.alert(
      'Preencha a "Quantidade" para adicionar um produto/item no estoque!'
    );
  } else if (preço.value <= 0) {
    window.alert(
      'Preencha o campo "Preço" para adicionar um produto/item no estoque!'
    );
  }
}
export default Verificar;
